import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { dashboardAPI, aiAPI } from '../services/api';
import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

export default function Dashboard() {
  const { user } = useAuth();
  const [summary, setSummary] = useState(mockSummary);
  const [insights, setInsights] = useState(mockInsights);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const load = async () => {
      try {
        const sum = await dashboardAPI.getSummary();
        if (sum) setSummary(sum);
      } catch {}
      try {
        const ins = await aiAPI.getInsights();
        if (ins?.length) setInsights(ins.slice(0, 3));
      } catch {}
      setLoading(false);
    };
    load();
  }, []);

  const firstName = user?.name?.split(' ')[0] || 'there';

  const stats = [
    { label: 'Total Spent', value: `₹${(summary?.totalSpent || 0).toLocaleString()}`, icon: '◎', color: 'var(--accent)' },
    { label: 'You Owe', value: `₹${(summary?.totalOwed || 0).toLocaleString()}`, icon: '↑', color: 'var(--danger)' },
    { label: 'Owed to You', value: `₹${(summary?.totalOwedToYou || 0).toLocaleString()}`, icon: '↓', color: 'var(--success)' },
    { label: 'Active Groups', value: summary?.activeGroups || 0, icon: '◈', color: 'var(--warning)' },
  ];

  return (
    <div style={styles.page}>
      <div style={styles.header}>
        <div>
          <h1 style={styles.greeting}>
            Good {getTimeOfDay()},{' '}
            <span style={styles.nameGradient}>{firstName}</span> ✦
          </h1>
          <p style={styles.subGreeting}>Here's your financial overview</p>
        </div>
        <div style={styles.dateBadge}>
          {new Date().toLocaleDateString('en-IN', { weekday: 'long', month: 'long', day: 'numeric' })}
        </div>
      </div>

      {/* Stats */}
      <div style={styles.statsGrid}>
        {stats.map((stat, i) => (
          <div key={i} className="card" style={{ ...styles.statCard, animationDelay: `${i * 0.08}s` }}>
            <div style={{ ...styles.statIcon, color: stat.color }}>{stat.icon}</div>
            <div style={styles.statValue}>{stat.value}</div>
            <div style={styles.statLabel}>{stat.label}</div>
            <div style={{ ...styles.statGlow, background: stat.color }} />
          </div>
        ))}
      </div>

      <div style={styles.mainGrid}>
        {/* Chart */}
        <div className="card" style={styles.chartCard}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Spending Overview</h3>
            <span className="badge badge-accent">Last 6 Months</span>
          </div>
          <ResponsiveContainer width="100%" height={200}>
            <AreaChart data={summary?.monthlyData || mockMonthly}>
              <defs>
                <linearGradient id="colorAmt" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#6c63ff" stopOpacity={0.3} />
                  <stop offset="95%" stopColor="#6c63ff" stopOpacity={0} />
                </linearGradient>
              </defs>
              <XAxis dataKey="month" tick={{ fill: '#8888aa', fontSize: 12 }} axisLine={false} tickLine={false} />
              <YAxis tick={{ fill: '#8888aa', fontSize: 12 }} axisLine={false} tickLine={false} width={55}
                tickFormatter={v => `₹${v >= 1000 ? (v/1000).toFixed(0)+'k' : v}`} />
              <Tooltip contentStyle={{ background: '#16161f', border: '1px solid #2a2a3a', borderRadius: 10, color: '#f0f0ff' }}
                formatter={v => [`₹${Number(v).toLocaleString()}`, 'Spent']} />
              <Area type="monotone" dataKey="amount" stroke="#6c63ff" strokeWidth={2} fill="url(#colorAmt)" />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* AI Insights */}
        <div className="card" style={styles.insightCard}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>✦ AI Insights</h3>
            <span className="badge badge-accent">Smart</span>
          </div>
          <div style={styles.insightsList}>
            {insights.map((insight, i) => (
              <div key={i} style={styles.insightItem}>
                <div style={styles.insightDot} />
                <div>
                  <div style={styles.insightType}>{insight.insightType || insight.type}</div>
                  <div style={styles.insightMsg}>{insight.message}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Pending Settlements */}
      {summary?.recentSettlements?.length > 0 && (
        <div className="card" style={styles.settlementsCard}>
          <div style={styles.cardHeader}>
            <h3 style={styles.cardTitle}>Pending Settlements</h3>
            <a href="/settlements" style={styles.viewAll}>View all →</a>
          </div>
          {summary.recentSettlements.slice(0, 4).map((s, i) => (
            <div key={i} style={styles.settlementRow}>
              <div className="avatar">{s.fromUser?.name?.[0] || '?'}</div>
              <div style={{ flex: 1 }}>
                <span style={{ color: 'var(--text-primary)', fontWeight: 500 }}>{s.fromUser?.name}</span>
                <span style={{ color: 'var(--text-muted)' }}> owes </span>
                <span style={{ color: 'var(--text-primary)', fontWeight: 500 }}>{s.toUser?.name}</span>
              </div>
              <span className="amount-negative">₹{s.amount?.toLocaleString()}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

function getTimeOfDay() {
  const h = new Date().getHours();
  if (h < 12) return 'morning';
  if (h < 17) return 'afternoon';
  return 'evening';
}

