import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { groupAPI } from '../services/api';
import toast from 'react-hot-toast';

// Must match backend Group.GroupType enum
const GROUP_TYPES = ['GENERAL','TRIP','HOME','FOOD','WORK','OTHER'];
const TYPE_META = {
  GENERAL:  { emoji: 'â—Ž',  label: 'General' },
  TRIP:     { emoji: 'âœˆï¸', label: 'Trip' },
  HOME:     { emoji: 'ðŸ ', label: 'Home' },
  FOOD:     { emoji: 'ðŸ½ï¸', label: 'Food' },
  WORK:     { emoji: 'ðŸ’¼', label: 'Work' },
  OTHER:    { emoji: 'ðŸ“¦', label: 'Other' },
};

export default function Groups() {
  const [groups, setGroups]       = useState([]);
  const [loading, setLoading]     = useState(true);
  const [showCreate, setShowCreate] = useState(false);
  const [creating, setCreating]   = useState(false);
  const [form, setForm] = useState({ name: '', description: '', type: 'GENERAL' });
  const navigate = useNavigate();

  useEffect(() => { loadGroups(); }, []);

  const loadGroups = async () => {
    setLoading(true);
    try {
      const data = await groupAPI.getAll();
      setGroups(Array.isArray(data) ? data : []);
    } catch (err) {
      toast.error('Could not load groups');
      setGroups([]);
    } finally { setLoading(false); }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    if (!form.name.trim()) { toast.error('Group name is required'); return; }
    setCreating(true);
    try {
      // Send exactly what backend expects: name, description, type
      await groupAPI.create({
        name: form.name.trim(),
        description: form.description.trim() || null,
        type: form.type,
        icon: TYPE_META[form.type]?.emoji || 'â—Ž',
        themeColor: '#6c63ff',
      });
      toast.success('Group created! ðŸŽ‰');
      setShowCreate(false);
      setForm({ name: '', description: '', type: 'GENERAL' });
      loadGroups();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to create group');
    } finally { setCreating(false); }
  };

  return (
    <div style={S.page}>
      <div style={S.header}>
        <div>
          <h1 style={S.title}>Groups</h1>
          <p style={S.subtitle}>{groups.length} group{groups.length !== 1 ? 's' : ''}</p>
        </div>
        <button className="btn btn-primary" onClick={() => setShowCreate(true)}>+ New Group</button>
      </div>

      {/* Create Modal */}
      {showCreate && (
        <div style={S.overlay} onClick={() => setShowCreate(false)}>
          <div style={S.modal} onClick={e => e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Create a Group</h2>
            <p style={S.modalSub}>Add friends after creating</p>
            <form onSubmit={handleCreate} style={S.form}>
              <div className="input-group">
                <label>Group Name *</label>
                <input className="input" placeholder="e.g. Goa Trip 2025"
                  value={form.name} onChange={e => setForm({...form, name: e.target.value})} required />
              </div>
              <div className="input-group">
                <label>Description</label>
                <input className="input" placeholder="Optional description"
                  value={form.description} onChange={e => setForm({...form, description: e.target.value})} />
              </div>
              <div className="input-group">
                <label>Type</label>
                <div style={S.typeGrid}>
                  {GROUP_TYPES.map(t => (
                    <button key={t} type="button"
                      style={{...S.typeBtn, ...(form.type === t ? S.typeBtnActive : {})}}
                      onClick={() => setForm({...form, type: t})}>
                      {TYPE_META[t].emoji} {TYPE_META[t].label}
                    </button>
                  ))}
                </div>
              </div>
              <div style={S.modalActions}>
                <button type="button" className="btn btn-ghost" onClick={() => setShowCreate(false)}>Cancel</button>
                <button type="submit" className="btn btn-primary" disabled={creating}>
                  {creating ? 'Creating...' : 'Create Group â†’'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Groups Grid */}
      {loading ? (
        <div style={S.grid}>
          {[1,2,3,4].map(i => <div key={i} className="skeleton" style={{height:180, borderRadius:16}} />)}
        </div>
      ) : groups.length === 0 ? (
        <div style={S.empty}>
          <div style={S.emptyIcon}>â—ˆ</div>
          <h3 style={S.emptyTitle}>No groups yet</h3>
          <p style={S.emptyText}>Create your first group and invite friends to start splitting</p>
          <button className="btn btn-primary" style={{marginTop:8}} onClick={() => setShowCreate(true)}>
            + Create First Group
          </button>
        </div>
      ) : (
        <div style={S.grid}>
          {groups.map((g, i) => (
            <div key={g.id} className="card" style={S.groupCard}
              onClick={() => navigate(`/groups/${g.id}`)}>
              <div style={S.groupTop}>
                <div style={S.groupEmoji}>{TYPE_META[g.type]?.emoji || 'â—Ž'}</div>
                <span className="badge badge-accent">{g.type || 'GENERAL'}</span>
              </div>
              <h3 style={S.groupName}>{g.name}</h3>
              <p style={S.groupDesc}>{g.description || 'No description'}</p>
              <div style={S.groupFooter}>
                <div style={S.members}>
                  <span style={{color:'var(--accent)'}}>â—‰</span>{' '}
                  {g.members?.length || 1} member{(g.members?.length || 1) !== 1 ? 's' : ''}
                </div>
                <div style={S.inviteCode}>
                  #{g.inviteCode}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

