import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { groupAPI } from '../services/api';
import toast from 'react-hot-toast';

const GROUP_TYPES = ['GENERAL','TRIP','HOME','FOOD','WORK','OTHER'];
const TYPE_META = {
  GENERAL: { emoji:'â—Ž',  label:'General' },
  TRIP:    { emoji:'âœˆï¸', label:'Trip' },
  HOME:    { emoji:'ðŸ ', label:'Home' },
  FOOD:    { emoji:'ðŸ½ï¸', label:'Food' },
  WORK:    { emoji:'ðŸ’¼', label:'Work' },
  OTHER:   { emoji:'ðŸ“¦', label:'Other' },
};

export default function Groups() {
  const [groups, setGroups]         = useState([]);
  const [loading, setLoading]       = useState(true);
  const [showCreate, setShowCreate] = useState(false);
  const [creating, setCreating]     = useState(false);
  const [form, setForm]             = useState({ name:'', description:'', type:'GENERAL' });
  const [formErr, setFormErr]       = useState('');
  const navigate = useNavigate();

  useEffect(() => { load(); }, []);

  const load = async () => {
    setLoading(true);
    try {
      const d = await groupAPI.getAll();
      setGroups(Array.isArray(d) ? d : []);
    } catch (err) {
      console.error('Load groups error:', err);
      setGroups([]);
    } finally { setLoading(false); }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setFormErr('');
    if (!form.name.trim()) { setFormErr('Group name is required'); return; }
    setCreating(true);
    try {
      await groupAPI.create({
        name:        form.name.trim(),
        description: form.description.trim() || null,
        type:        form.type,          // GENERAL, TRIP, HOME, FOOD, WORK, OTHER
        icon:        TYPE_META[form.type]?.emoji,
        themeColor:  '#6c63ff',
      });
      toast.success('Group created! ðŸŽ‰');
      setShowCreate(false);
      setForm({ name:'', description:'', type:'GENERAL' });
      load();
    } catch (err) {
      const msg = err.response?.data?.message || err.message || 'Failed to create group';
      setFormErr(msg);
      toast.error(msg);
      console.error('Create group error:', err.response?.data || err);
    } finally { setCreating(false); }
  };

  return (
    <div style={S.page}>
      <div style={S.header}>
        <div>
          <h1 style={S.title}>Groups</h1>
          <p style={S.sub}>{groups.length} group{groups.length!==1?'s':''}</p>
        </div>
        <button className="btn btn-primary" onClick={()=>{ setFormErr(''); setShowCreate(true); }}>
          + New Group
        </button>
      </div>

      {/* CREATE MODAL */}
      {showCreate && (
        <div style={S.overlay} onClick={()=>setShowCreate(false)}>
          <div style={S.modal} onClick={e=>e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Create Group</h2>
            <form onSubmit={handleCreate} style={S.form}>
              <div className="input-group">
                <label>Group Name *</label>
                <input className="input" placeholder="e.g. Goa Trip 2025"
                  value={form.name} autoFocus
                  onChange={e=>{ setForm({...form,name:e.target.value}); setFormErr(''); }}
                  style={formErr&&!form.name.trim()?{borderColor:'var(--danger)'}:{}} />
              </div>
              <div className="input-group">
                <label>Description <span style={{color:'var(--text-muted)',fontWeight:400}}>(optional)</span></label>
                <input className="input" placeholder="What's this group for?"
                  value={form.description} onChange={e=>setForm({...form,description:e.target.value})} />
              </div>
              <div className="input-group">
                <label>Type</label>
                <div style={S.typeGrid}>
                  {GROUP_TYPES.map(t=>(
                    <button key={t} type="button"
                      style={{...S.typeBtn,...(form.type===t?S.typeBtnActive:{})}}
                      onClick={()=>setForm({...form,type:t})}>
                      {TYPE_META[t].emoji} {TYPE_META[t].label}
                    </button>
                  ))}
                </div>
              </div>
              {formErr && <div style={S.errBox}>âš  {formErr}</div>}
              <div style={S.actions}>
                <button type="button" className="btn btn-ghost" onClick={()=>setShowCreate(false)}>Cancel</button>
                <button type="submit" className="btn btn-primary" disabled={creating}>
                  {creating ? 'Creating...' : 'Create Group â†’'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* GROUPS GRID */}
      {loading ? (
        <div style={S.grid}>
          {[1,2,3,4].map(i=><div key={i} className="skeleton" style={{height:180,borderRadius:16}}/>)}
        </div>
      ) : groups.length === 0 ? (
        <div style={S.empty}>
          <div style={{fontSize:52,marginBottom:8}}>â—ˆ</div>
          <h3 style={S.emptyTitle}>No groups yet</h3>
          <p style={S.emptySub}>Create your first group and start splitting expenses</p>
          <button className="btn btn-primary" style={{marginTop:8}} onClick={()=>setShowCreate(true)}>
            + Create First Group
          </button>
        </div>
      ) : (
        <div style={S.grid}>
          {groups.map((g,i)=>(
            <div key={g.id} className="card" style={S.card} onClick={()=>navigate(`/groups/${g.id}`)}>
              <div style={S.cardTop}>
                <div style={S.emoji}>{TYPE_META[g.type]?.emoji||'â—Ž'}</div>
                <span className="badge badge-accent">{g.type||'GENERAL'}</span>
              </div>
              <h3 style={S.cardName}>{g.name}</h3>
              <p style={S.cardDesc}>{g.description||'No description'}</p>
              <div style={S.cardFoot}>
                <span style={S.members}>â—‰ {g.members?.length||1} member{(g.members?.length||1)!==1?'s':''}</span>
                <span style={S.code}>#{g.inviteCode}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

