import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { dashboardAPI } from '../services/api';
import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';

const PIE_COLORS = ['#6c63ff','#00d4aa','#ff6b6b','#ffb347','#9b93ff','#ff9ff3'];

export default function Dashboard() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [data, setData]     = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => { load(); }, []);

  const load = async () => {
    setLoading(true);
    try {
      const d = await dashboardAPI.get();
      setData(d);
    } catch (err) {
      console.error('Dashboard load failed:', err);
    } finally { setLoading(false); }
  };

  const firstName = user?.name?.split(' ')[0] || 'there';
  const hour = new Date().getHours();
  const greeting = hour < 12 ? 'Good morning' : hour < 17 ? 'Good afternoon' : 'Good evening';

  // Map backend fields to display
  const totalYouOwe    = Number(data?.totalYouOwe    || 0);
  const totalYouGet    = Number(data?.totalYouGet    || 0);
  const activeGroups   = Number(data?.activeGroups   || 0);
  const totalExpenses  = Number(data?.totalExpenses  || 0);
  const rewardPoints   = Number(data?.rewardPoints   || 0);
  const zeroDebtStreak = Number(data?.zeroDebtStreak || 0);

  const stats = [
    { label:'You Owe',      value:`‚Çπ${totalYouOwe.toLocaleString()}`,  icon:'‚Üë', color:'var(--danger)',  sub:'pending payments' },
    { label:'Owed to You',  value:`‚Çπ${totalYouGet.toLocaleString()}`,  icon:'‚Üì', color:'var(--success)', sub:'to collect' },
    { label:'Active Groups',value: activeGroups,                        icon:'‚óà', color:'var(--accent)',  sub:'groups joined' },
    { label:'Expenses',     value: totalExpenses,                       icon:'‚óé', color:'var(--warning)', sub:'this month' },
  ];

  // Monthly chart data
  const chartData = (data?.monthlyTrend || []).map(m => ({
    month: m.month?.slice(0,3),
    amount: Number(m.amount || 0)
  }));

  // Category pie data
  const pieData = Object.entries(data?.categoryBreakdown || {}).map(([name, value]) => ({
    name, value: Number(value)
  })).filter(d => d.value > 0);

  // Pending settlements
  const pendingSettlements = data?.pendingSettlements || [];

  // Recent expenses
  const recentExpenses = data?.recentExpenses || [];

  return (
    <div style={S.page}>
      {/* Header */}
      <div style={S.header}>
        <div>
          <h1 style={S.greeting}>
            {greeting},{' '}
            <span style={S.nameGrad}>{firstName}</span> ‚ú¶
          </h1>
          <p style={S.date}>{new Date().toLocaleDateString('en-IN',{weekday:'long',day:'numeric',month:'long'})}</p>
        </div>
        <div style={S.streakBadge}>
          <div style={S.streakIcon}>üî•</div>
          <div>
            <div style={S.streakNum}>{zeroDebtStreak} days</div>
            <div style={S.streakLabel}>Zero Debt Streak</div>
          </div>
          <div style={S.rewardPts}>‚≠ê {rewardPoints} pts</div>
        </div>
      </div>

      {/* Stats Grid */}
      <div style={S.statsGrid}>
        {stats.map((s,i) => (
          <div key={i} className="card" style={{...S.statCard, animationDelay:`${i*0.07}s`}}>
            <div style={{...S.statIcon, color:s.color}}>{s.icon}</div>
            <div style={S.statValue}>{loading ? '‚Äî' : s.value}</div>
            <div style={S.statLabel}>{s.label}</div>
            <div style={S.statSub}>{s.sub}</div>
            <div style={{...S.statGlow, background:s.color}} />
          </div>
        ))}
      </div>

      <div style={S.row}>
        {/* Monthly Chart */}
        <div className="card" style={S.chartCard}>
          <div style={S.cardHead}>
            <h3 style={S.cardTitle}>Monthly Spending</h3>
            <span className="badge badge-accent">This Year</span>
          </div>
          {chartData.length === 0 ? (
            <div style={S.emptyChart}>
              <p style={{color:'var(--text-muted)', fontSize:14}}>No expense data yet.<br/>Create a group and add expenses!</p>
              <button className="btn btn-primary" style={{marginTop:12}} onClick={()=>navigate('/groups')}>
                + Create Group
              </button>
            </div>
          ) : (
            <ResponsiveContainer width="100%" height={200}>
              <AreaChart data={chartData}>
                <defs>
                  <linearGradient id="grad" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%"  stopColor="#6c63ff" stopOpacity={0.3}/>
                    <stop offset="95%" stopColor="#6c63ff" stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <XAxis dataKey="month" tick={{fill:'#8888aa',fontSize:11}} axisLine={false} tickLine={false}/>
                <YAxis tick={{fill:'#8888aa',fontSize:11}} axisLine={false} tickLine={false} width={50}
                  tickFormatter={v=>`‚Çπ${v>=1000?(v/1000).toFixed(0)+'k':v}`}/>
                <Tooltip contentStyle={{background:'#16161f',border:'1px solid #2a2a3a',borderRadius:10,color:'#f0f0ff',fontSize:13}}
                  formatter={v=>[`‚Çπ${Number(v).toLocaleString()}`,'Spent']}/>
                <Area type="monotone" dataKey="amount" stroke="#6c63ff" strokeWidth={2} fill="url(#grad)"/>
              </AreaChart>
            </ResponsiveContainer>
          )}
        </div>

        {/* Category Pie */}
        <div className="card" style={S.pieCard}>
          <div style={S.cardHead}>
            <h3 style={S.cardTitle}>This Month</h3>
          </div>
          {pieData.length === 0 ? (
            <div style={S.emptyChart}>
              <p style={{color:'var(--text-muted)', fontSize:13, textAlign:'center'}}>No categories yet</p>
            </div>
          ) : (
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie data={pieData} cx="50%" cy="50%" innerRadius={50} outerRadius={80} dataKey="value"
                  paddingAngle={3}>
                  {pieData.map((_,i) => <Cell key={i} fill={PIE_COLORS[i%PIE_COLORS.length]}/>)}
                </Pie>
                <Legend iconType="circle" iconSize={8} wrapperStyle={{fontSize:11,color:'#8888aa'}}/>
                <Tooltip formatter={v=>[`‚Çπ${Number(v).toLocaleString()}`]}
                  contentStyle={{background:'#16161f',border:'1px solid #2a2a3a',borderRadius:10,fontSize:12}}/>
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>
      </div>

      <div style={S.row}>
        {/* Pending Settlements */}
        <div className="card" style={S.settleCard}>
          <div style={S.cardHead}>
            <h3 style={S.cardTitle}>Pending Settlements</h3>
            <button style={S.viewAll} onClick={()=>navigate('/settlements')}>View all ‚Üí</button>
          </div>
          {pendingSettlements.length === 0 ? (
            <div style={S.emptySmall}>
              <span style={{fontSize:24}}>üéâ</span>
              <p style={{color:'var(--text-muted)',fontSize:13}}>All settled up!</p>
            </div>
          ) : pendingSettlements.slice(0,4).map((s,i) => (
            <div key={s.id||i} style={S.settleRow}>
              <div className="avatar" style={S.avatarFrom}>{s.fromUser?.name?.[0]||'?'}</div>
              <div style={{flex:1}}>
                <span style={{fontWeight:600,color:'var(--text-primary)',fontSize:14}}>{s.fromUser?.name}</span>
                <span style={{color:'var(--text-muted)',fontSize:13}}> owes </span>
                <span style={{fontWeight:600,color:'var(--text-primary)',fontSize:14}}>{s.toUser?.name}</span>
                <div style={{fontSize:11,color:'var(--text-muted)'}}>{s.groupName}</div>
              </div>
              <div style={{textAlign:'right'}}>
                <div style={{fontFamily:'var(--font-display)',fontWeight:700,color:'var(--danger)',fontSize:16}}>
                  ‚Çπ{Number(s.amount||0).toLocaleString()}
                </div>
                {/* UPI Pay buttons */}
                {s.googlePayLink && (
                  <div style={S.payBtns}>
                    <a href={s.googlePayLink} style={{...S.payBtn, background:'#1a73e8'}}>GPay</a>
                    {s.phonePeLink && <a href={s.phonePeLink} style={{...S.payBtn, background:'#5f259f'}}>PhonePe</a>}
                    {s.paytmLink   && <a href={s.paytmLink}   style={{...S.payBtn, background:'#00b9f1'}}>Paytm</a>}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Recent Expenses */}
        <div className="card" style={S.recentCard}>
          <div style={S.cardHead}>
            <h3 style={S.cardTitle}>Recent Expenses</h3>
            <button style={S.viewAll} onClick={()=>navigate('/groups')}>View groups ‚Üí</button>
          </div>
          {recentExpenses.length === 0 ? (
            <div style={S.emptySmall}>
              <span style={{fontSize:24}}>‚óé</span>
              <p style={{color:'var(--text-muted)',fontSize:13}}>No expenses yet</p>
              <button className="btn btn-primary" style={{fontSize:12,padding:'6px 14px'}} onClick={()=>navigate('/groups')}>
                + Add Expense
              </button>
            </div>
          ) : recentExpenses.slice(0,5).map((e,i) => (
            <div key={e.id||i} style={S.expRow}>
              <div style={S.expCat}>{CAT_EMOJI[e.category]||'‚óé'}</div>
              <div style={{flex:1}}>
                <div style={{fontWeight:600,fontSize:13,color:'var(--text-primary)'}}>{e.description}</div>
                <div style={{fontSize:11,color:'var(--text-muted)'}}>{e.groupName} ¬∑ {e.paidBy?.name}</div>
              </div>
              <div style={{fontFamily:'var(--font-display)',fontWeight:700,fontSize:15,color:'var(--text-primary)'}}>
                ‚Çπ{Number(e.amount||0).toLocaleString()}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* AI Insights strip */}
      {(data?.aiInsights||[]).length > 0 && (
        <div className="card" style={S.insightStrip}>
          <div style={S.cardHead}>
            <h3 style={S.cardTitle}>‚ú¶ AI Insights</h3>
            <button style={S.viewAll} onClick={()=>navigate('/ai')}>See all ‚Üí</button>
          </div>
          <div style={S.insightRow}>
            {(data.aiInsights||[]).slice(0,3).map((ins,i)=>(
              <div key={i} style={S.insightChip}>
                <span style={{color:'var(--accent)'}}>{ins.insightType?.replace('_',' ')}</span>
                <span style={{color:'var(--text-secondary)',fontSize:12}}>: {ins.message}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Quick Actions ‚Äî shown when everything is 0 */}
      {!loading && activeGroups === 0 && (
        <div style={S.quickStart}>
          <h3 style={S.qsTitle}>üëã Get started in 3 steps</h3>
          <div style={S.qsGrid}>
            {[
              { step:'1', title:'Create a Group', desc:'Goa trip, flat mates, office lunch...', btn:'Create Group', to:'/groups' },
              { step:'2', title:'Add Members',    desc:'Invite friends by searching their email', btn:'Go to Groups', to:'/groups' },
              { step:'3', title:'Add Expenses',   desc:'Split bills equally or by amount', btn:'Add Expense', to:'/groups' },
            ].map((q,i)=>(
              <div key={i} className="card" style={S.qsCard}>
                <div style={S.qsStep}>{q.step}</div>
                <div style={S.qsCardTitle}>{q.title}</div>
                <p style={{fontSize:13,color:'var(--text-muted)',lineHeight:1.5,marginBottom:16}}>{q.desc}</p>
                <button className="btn btn-primary" style={{fontSize:13}} onClick={()=>navigate(q.to)}>{q.btn}</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

