import React, { useState, useEffect } from 'react';
import { settlementAPI, groupAPI } from '../services/api';
import toast from 'react-hot-toast';

export default function Settlements() {
  const [groups, setGroups] = useState([]);
  const [settlements, setSettlements] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => { loadGroups(); }, []);
  useEffect(() => { if (selectedGroup) loadSettlements(selectedGroup); }, [selectedGroup]);

  const loadGroups = async () => {
    try {
      const data = await groupAPI.getAll();
      const list = Array.isArray(data) ? data : (data?.content || mockGroups);
      setGroups(list);
      if (list.length > 0) setSelectedGroup(list[0].id);
    } catch {
      setGroups(mockGroups);
      setSelectedGroup(mockGroups[0].id);
    }
  };

  const loadSettlements = async (groupId) => {
    setLoading(true);
    try {
      const data = await settlementAPI.getByGroup(groupId);
      const list = Array.isArray(data) ? data : [];
      setSettlements(list.length > 0 ? list : mockSettlements);
    } catch {
      setSettlements(mockSettlements);
    } finally {
      setLoading(false);
    }
  };

  const markPaid = async (id) => {
    try {
      await settlementAPI.markPaid(id);
      toast.success('Settlement marked as paid! ðŸŽ‰');
      setSettlements(prev => prev.map(s => s.id === id ? { ...s, status: 'PAID' } : s));
    } catch {
      toast.error('Failed to update settlement');
    }
  };

  const pending = settlements.filter(s => s.status !== 'PAID' && s.status !== 'COMPLETED');
  const paid = settlements.filter(s => s.status === 'PAID' || s.status === 'COMPLETED');
  const totalPending = pending.reduce((sum, s) => sum + (s.amount || 0), 0);

  return (
    <div style={styles.page}>
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>Settlements</h1>
          <p style={styles.subtitle}>Simplified debt repayment</p>
        </div>
        {totalPending > 0 && (
          <div style={styles.totalBadge}>
            <span style={styles.totalLabel}>TOTAL PENDING</span>
            <span style={styles.totalAmount}>â‚¹{totalPending.toLocaleString()}</span>
          </div>
        )}
      </div>

      <div style={styles.tabs}>
        {groups.map(g => (
          <button key={g.id}
            style={{ ...styles.tab, ...(selectedGroup === g.id ? styles.tabActive : {}) }}
            onClick={() => setSelectedGroup(g.id)}>
            {g.name}
          </button>
        ))}
      </div>

      {loading ? (
