import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { groupAPI, expenseAPI } from '../services/api';
import toast from 'react-hot-toast';

const CATEGORIES = ['GENERAL','FOOD','TRANSPORT','ENTERTAINMENT','SHOPPING','UTILITIES','MEDICAL','TRAVEL','RENT','OTHER'];
const SPLIT_TYPES = ['EQUAL','PERCENTAGE','CUSTOM'];
const CAT_EMOJI = { GENERAL:'‚óé', FOOD:'üçΩÔ∏è', TRANSPORT:'üöó', ENTERTAINMENT:'üéÆ', SHOPPING:'üõçÔ∏è', UTILITIES:'üí°', MEDICAL:'üè•', TRAVEL:'‚úàÔ∏è', RENT:'üè†', OTHER:'üì¶' };

export default function GroupDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [group, setGroup]       = useState(null);
  const [members, setMembers]   = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [tab, setTab]           = useState('expenses'); // expenses | members
  const [showAddExp, setShowAddExp] = useState(false);
  const [showAddMember, setShowAddMember] = useState(false);
  const [searchQ, setSearchQ]   = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [expForm, setExpForm]   = useState({ description:'', amount:'', category:'FOOD', splitType:'EQUAL', participantIds:[] });
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => { loadAll(); }, [id]);

  const loadAll = async () => {
    setLoading(true);
    try {
      const [grp, mem, exp] = await Promise.all([
        groupAPI.getById(id),
        groupAPI.getMembers(id),
        expenseAPI.getByGroup(id),
      ]);
      setGroup(grp);
      const memList = Array.isArray(mem) ? mem : [];
      setMembers(memList);
      // Pre-select all members for expense
      setExpForm(f => ({ ...f, participantIds: memList.map(m => m.id) }));
      const expList = Array.isArray(exp) ? exp : (exp?.content || []);
      setExpenses(expList);
    } catch (err) {
      toast.error('Failed to load group');
    } finally { setLoading(false); }
  };

  const handleSearch = async (q) => {
    setSearchQ(q);
    if (q.length < 2) { setSearchResults([]); return; }
    try {
      const res = await groupAPI.searchUsers(q);
      setSearchResults(Array.isArray(res) ? res : []);
    } catch { setSearchResults([]); }
  };

  const handleAddMember = async (userId) => {
    try {
      await groupAPI.addMember(id, userId);
      toast.success('Member added!');
      setShowAddMember(false);
      setSearchQ('');
      setSearchResults([]);
      loadAll();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to add member');
    }
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await expenseAPI.create({
        description: expForm.description,
        amount: parseFloat(expForm.amount),
        groupId: parseInt(id),
        splitType: expForm.splitType,
        category: expForm.category,
        participantIds: expForm.participantIds.length > 0 ? expForm.participantIds : members.map(m => m.id),
      });
      toast.success('Expense added! ‚úì');
      setShowAddExp(false);
      setExpForm({ description:'', amount:'', category:'FOOD', splitType:'EQUAL', participantIds: members.map(m => m.id) });
      loadAll();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to add expense');
    } finally { setSubmitting(false); }
  };

  const toggleParticipant = (uid) => {
    setExpForm(f => ({
      ...f,
      participantIds: f.participantIds.includes(uid)
        ? f.participantIds.filter(id => id !== uid)
        : [...f.participantIds, uid]
    }));
  };

  if (loading) return (
    <div style={{padding:32}}>
      <div className="skeleton" style={{height:60, marginBottom:24, borderRadius:12}} />
      {[1,2,3].map(i => <div key={i} className="skeleton" style={{height:80, borderRadius:12, marginBottom:12}} />)}
    </div>
  );

  if (!group) return <div style={{padding:32, color:'var(--text-muted)'}}>Group not found.</div>;

  const totalExpenses = expenses.reduce((s, e) => s + (e.amount || 0), 0);

  return (
    <div style={S.page}>
      {/* Back + Header */}
      <button style={S.backBtn} onClick={() => navigate('/groups')}>‚Üê Back to Groups</button>

      <div style={S.header}>
        <div>
          <h1 style={S.title}>{group.name}</h1>
          <p style={S.subtitle}>{group.description || 'No description'} ¬∑ #{group.inviteCode}</p>
        </div>
        <div style={S.headerRight}>
          <div style={S.statPill}>
            <span style={{color:'var(--text-muted)', fontSize:12}}>Total</span>
            <span style={{fontFamily:'var(--font-display)', fontWeight:700, color:'var(--accent-bright)'}}>
              ‚Çπ{totalExpenses.toLocaleString()}
            </span>
          </div>
          <button className="btn btn-primary" onClick={() => setShowAddExp(true)}>+ Add Expense</button>
        </div>
      </div>

      {/* Tabs */}
      <div style={S.tabs}>
        {['expenses','members'].map(t => (
          <button key={t} style={{...S.tab, ...(tab===t ? S.tabActive:{})}} onClick={() => setTab(t)}>
            {t === 'expenses' ? `Expenses (${expenses.length})` : `Members (${members.length})`}
          </button>
        ))}
      </div>

      {/* Members Tab */}
      {tab === 'members' && (
        <div style={S.memberSection}>
          <button className="btn btn-ghost" style={{marginBottom:16}} onClick={() => setShowAddMember(true)}>
            + Add Member
          </button>
          {members.map((m, i) => (
            <div key={m.id} className="card" style={S.memberRow}>
              <div className="avatar">{m.name?.[0]?.toUpperCase() || '?'}</div>
              <div style={{flex:1}}>
                <div style={{fontWeight:600, color:'var(--text-primary)'}}>{m.name}</div>
                <div style={{fontSize:12, color:'var(--text-muted)'}}>{m.email}</div>
              </div>
            </div>
          ))}
          {members.length === 0 && (
            <div style={{color:'var(--text-muted)', textAlign:'center', padding:'40px 0'}}>No members yet</div>
          )}
        </div>
      )}

      {/* Expenses Tab */}
      {tab === 'expenses' && (
        <div style={S.expList}>
          {expenses.length === 0 ? (
            <div style={S.empty}>
              <div style={{fontSize:40}}>‚óé</div>
              <h3 style={{fontFamily:'var(--font-display)', fontWeight:700, fontSize:18, color:'var(--text-primary)'}}>No expenses yet</h3>
              <p style={{color:'var(--text-muted)', fontSize:14}}>Add the first expense to this group</p>
              <button className="btn btn-primary" style={{marginTop:8}} onClick={() => setShowAddExp(true)}>+ Add Expense</button>
            </div>
          ) : expenses.map((exp, i) => (
            <div key={exp.id || i} className="card" style={S.expRow}>
              <div style={S.expIcon}>{CAT_EMOJI[exp.category] || '‚óé'}</div>
              <div style={{flex:1}}>
                <div style={{fontWeight:600, color:'var(--text-primary)', marginBottom:4}}>{exp.description}</div>
                <div style={{fontSize:12, color:'var(--text-muted)'}}>
                  Paid by <span style={{color:'var(--accent-bright)'}}>{exp.paidBy?.name || 'You'}</span>
                  {' ¬∑ '}{exp.splitType}
                  {' ¬∑ '}{new Date(exp.expenseDate || exp.createdAt).toLocaleDateString('en-IN')}
                </div>
              </div>
              <div style={{textAlign:'right'}}>
                <div style={{fontFamily:'var(--font-display)', fontWeight:700, fontSize:18, color:'var(--text-primary)'}}>
                  ‚Çπ{Number(exp.amount).toLocaleString()}
                </div>
                {exp.yourShare != null && (
                  <div style={{fontSize:12, color:'var(--text-muted)'}}>Your share: ‚Çπ{Number(exp.yourShare).toLocaleString()}</div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Add Expense Modal */}
      {showAddExp && (
        <div style={S.overlay} onClick={() => setShowAddExp(false)}>
          <div style={S.modal} onClick={e => e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Add Expense</h2>
            <form onSubmit={handleAddExpense} style={S.form}>
              <div className="input-group">
                <label>Description *</label>
                <input className="input" placeholder="e.g. Hotel booking"
                  value={expForm.description} onChange={e => setExpForm({...expForm, description:e.target.value})} required />
              </div>
              <div style={{display:'grid', gridTemplateColumns:'1fr 1fr', gap:14}}>
                <div className="input-group">
                  <label>Amount (‚Çπ) *</label>
                  <input className="input" type="number" placeholder="0.00" min="0.01" step="0.01"
                    value={expForm.amount} onChange={e => setExpForm({...expForm, amount:e.target.value})} required />
                </div>
                <div className="input-group">
                  <label>Category</label>
                  <select className="input" value={expForm.category}
                    onChange={e => setExpForm({...expForm, category:e.target.value})}>
                    {CATEGORIES.map(c => <option key={c} value={c}>{CAT_EMOJI[c]} {c}</option>)}
                  </select>
                </div>
              </div>
              <div className="input-group">
                <label>Split Type</label>
                <div style={S.typeGrid}>
                  {SPLIT_TYPES.map(t => (
                    <button key={t} type="button"
                      style={{...S.typeBtn, ...(expForm.splitType===t ? S.typeBtnActive:{})}}
                      onClick={() => setExpForm({...expForm, splitType:t})}>
                      {t}
                    </button>
                  ))}
                </div>
              </div>
              {members.length > 0 && (
                <div className="input-group">
                  <label>Split Between</label>
                  <div style={S.participantGrid}>
                    {members.map(m => (
                      <button key={m.id} type="button"
                        style={{...S.participantBtn, ...(expForm.participantIds.includes(m.id) ? S.participantActive:{})}}
                        onClick={() => toggleParticipant(m.id)}>
                        <div style={S.participantAvatar}>{m.name?.[0]?.toUpperCase()}</div>
                        {m.name}
                      </button>
                    ))}
                  </div>
                </div>
              )}
              <div style={S.modalActions}>
                <button type="button" className="btn btn-ghost" onClick={() => setShowAddExp(false)}>Cancel</button>
                <button type="submit" className="btn btn-primary" disabled={submitting}>
                  {submitting ? 'Adding...' : 'Add Expense ‚Üí'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Member Modal */}
      {showAddMember && (
        <div style={S.overlay} onClick={() => setShowAddMember(false)}>
          <div style={{...S.modal, maxWidth:400}} onClick={e => e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Add Member</h2>
            <p style={{fontSize:13, color:'var(--text-muted)', marginBottom:20}}>
              Share invite code: <span style={{color:'var(--accent-bright)', fontWeight:700}}>#{group.inviteCode}</span>
              {' '}or search by name/email below
            </p>
            <input className="input" placeholder="Search by name or email..."
              value={searchQ} onChange={e => handleSearch(e.target.value)} style={{marginBottom:12}} />
            <div style={{display:'flex', flexDirection:'column', gap:8, maxHeight:240, overflowY:'auto'}}>
              {searchResults.map(u => (
                <div key={u.id} style={S.searchResult}>
                  <div className="avatar">{u.name?.[0]?.toUpperCase()}</div>
                  <div style={{flex:1}}>
                    <div style={{fontWeight:600, color:'var(--text-primary)'}}>{u.name}</div>
                    <div style={{fontSize:12, color:'var(--text-muted)'}}>{u.email}</div>
                  </div>
                  <button className="btn btn-primary" style={{padding:'6px 14px', fontSize:13}}
                    onClick={() => handleAddMember(u.id)}>Add</button>
                </div>
              ))}
              {searchQ.length >= 2 && searchResults.length === 0 && (
                <div style={{color:'var(--text-muted)', fontSize:13, textAlign:'center', padding:'20px 0'}}>No users found</div>
              )}
            </div>
            <button type="button" className="btn btn-ghost" style={{marginTop:16, width:'100%'}}
              onClick={() => setShowAddMember(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

const S = {
  page:       { padding:32, maxWidth:900, animation:'fadeUp 0.4s ease' },
  backBtn:    { background:'none', border:'none', color:'var(--text-muted)', cursor:'pointer', fontSize:14, padding:'0 0 20px 0', display:'block' },
  header:     { display:'flex', justifyContent:'space-between', alignItems:'flex-start', marginBottom:24, flexWrap:'wrap', gap:12 },
  title:      { fontFamily:'var(--font-display)', fontWeight:800, fontSize:28, color:'var(--text-primary)', marginBottom:4 },
  subtitle:   { color:'var(--text-muted)', fontSize:14 },
  headerRight:{ display:'flex', alignItems:'center', gap:12 },
  statPill:   { display:'flex', flexDirection:'column', alignItems:'flex-end', background:'var(--bg-card)', border:'1px solid var(--border)', borderRadius:12, padding:'8px 16px' },
