import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { groupAPI, expenseAPI, settlementAPI } from '../services/api';
import toast from 'react-hot-toast';

const CAT_EMOJI={GENERAL:'â—Ž',FOOD:'ðŸ½ï¸',TRANSPORT:'ðŸš—',ENTERTAINMENT:'ðŸŽ®',SHOPPING:'ðŸ›ï¸',UTILITIES:'ðŸ’¡',MEDICAL:'ðŸ¥',TRAVEL:'âœˆï¸',RENT:'ðŸ ',OTHER:'ðŸ“¦'};
const CATEGORIES=['GENERAL','FOOD','TRANSPORT','ENTERTAINMENT','SHOPPING','UTILITIES','MEDICAL','TRAVEL','RENT','OTHER'];

const SPLIT_INFO = {
  EQUAL:      { label:'Equal Split',      desc:'Divide total equally among selected members', icon:'Ã·' },
  CUSTOM:     { label:'Custom Amounts',   desc:'You decide exact amount each person pays',    icon:'âœŽ' },
  PERCENTAGE: { label:'By Percentage',    desc:'Assign % share to each person (must = 100%)', icon:'%' },
  PARTIAL:    { label:'Partial (some pay)',desc:'Only selected members pay â€” e.g. 2 of 4 pay', icon:'â—‘' },
};

export default function GroupDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [group, setGroup]       = useState(null);
  const [members, setMembers]   = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [settlements, setSettlements] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [tab, setTab]           = useState('expenses');
  const [showAddExp, setShowAddExp]       = useState(false);
  const [showAddMember, setShowAddMember] = useState(false);
  const [searchQ, setSearchQ]   = useState('');
  const [searchRes, setSearchRes] = useState([]);
  const [submitting, setSubmitting] = useState(false);

  // Expense form
  const [form, setForm] = useState({
    description: '', amount: '', category: 'FOOD', splitType: 'EQUAL',
    participantIds: [],   // for EQUAL / PARTIAL
    customSplits: [],     // for CUSTOM: [{userId, amount}]
    percentageSplits: [], // for PERCENTAGE: [{userId, percentage}]
  });

  useEffect(() => { loadAll(); }, [id]);

  const loadAll = async () => {
    setLoading(true);
    try {
      const [grp, mem, exp] = await Promise.all([
        groupAPI.getById(id),
        groupAPI.getMembers(id),
        expenseAPI.getByGroup(id),
      ]);
      setGroup(grp);
      const ml = Array.isArray(mem) ? mem : [];
      setMembers(ml);
      const el = Array.isArray(exp) ? exp : (exp?.content || []);
      setExpenses(el);
      // Try loading settlements too
      try {
        const sl = await settlementAPI.getByGroup(id);
        setSettlements(Array.isArray(sl) ? sl : []);
      } catch {}
      // Init form participants to all members
      setForm(f => ({ ...f, participantIds: ml.map(m=>m.id), customSplits: ml.map(m=>({userId:m.id,amount:''})), percentageSplits: ml.map(m=>({userId:m.id,percentage:''})) }));
    } catch { toast.error('Failed to load group'); }
    finally { setLoading(false); }
  };

  // Search users
  const handleSearch = async (q) => {
    setSearchQ(q);
    if (q.length < 2) { setSearchRes([]); return; }
    try {
      const res = await groupAPI.searchUsers(q);
      // Filter out already-members
      const memberIds = new Set(members.map(m=>m.id));
      setSearchRes((Array.isArray(res)?res:[]).filter(u=>!memberIds.has(u.id)));
    } catch { setSearchRes([]); }
  };

  const addMember = async (userId) => {
    try {
      await groupAPI.addMember(id, userId);
      toast.success('Member added!');
      setShowAddMember(false); setSearchQ(''); setSearchRes([]);
      loadAll();
    } catch (err) { toast.error(err.response?.data?.message||'Failed to add member'); }
  };

  // Toggle participant for EQUAL/PARTIAL
  const toggleParticipant = (uid) => {
    setForm(f => ({
      ...f,
      participantIds: f.participantIds.includes(uid)
        ? f.participantIds.filter(i=>i!==uid)
        : [...f.participantIds, uid]
    }));
  };

  // Split type change â€” reset splits to all members
  const changeSplitType = (type) => {
    setForm(f => ({
      ...f, splitType: type,
      participantIds: members.map(m=>m.id),
      customSplits: members.map(m=>({userId:m.id, amount:''})),
      percentageSplits: members.map(m=>({userId:m.id, percentage:''})),
    }));
  };

  // Validate and auto-calc equal amount
  const getEqualShare = () => {
    const n = form.participantIds.length;
    const amt = parseFloat(form.amount);
    if (!n || !amt) return null;
    return (amt / n).toFixed(2);
  };

  const validateForm = () => {
    if (!form.description.trim()) return 'Description required';
    const amt = parseFloat(form.amount);
    if (!amt || amt <= 0) return 'Valid amount required';
    if (form.splitType === 'EQUAL' || form.splitType === 'PARTIAL') {
      if (form.participantIds.length === 0) return 'Select at least one participant';
    }
    if (form.splitType === 'CUSTOM') {
      const total = form.customSplits.filter(s=>s.amount).reduce((s,x)=>s+parseFloat(x.amount||0),0);
      if (Math.abs(total - amt) > 0.5) return `Custom amounts must sum to â‚¹${amt} (currently â‚¹${total.toFixed(2)})`;
    }
    if (form.splitType === 'PERCENTAGE') {
      const total = form.percentageSplits.filter(s=>s.percentage).reduce((s,x)=>s+parseFloat(x.percentage||0),0);
      if (Math.abs(total - 100) > 0.5) return `Percentages must sum to 100% (currently ${total.toFixed(1)}%)`;
    }
    return null;
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    const err = validateForm();
    if (err) { toast.error(err); return; }
    setSubmitting(true);
    try {
      const payload = {
        description: form.description.trim(),
        amount: parseFloat(form.amount),
        groupId: parseInt(id),
        splitType: form.splitType,
        category: form.category,
      };
      if (form.splitType === 'EQUAL' || form.splitType === 'PARTIAL') {
        payload.participantIds = form.participantIds;
      } else if (form.splitType === 'CUSTOM') {
        payload.customSplits = form.customSplits
          .filter(s => s.amount && parseFloat(s.amount) > 0)
          .map(s => ({ userId: s.userId, amount: parseFloat(s.amount) }));
      } else if (form.splitType === 'PERCENTAGE') {
        payload.percentageSplits = form.percentageSplits
          .filter(s => s.percentage && parseFloat(s.percentage) > 0)
          .map(s => ({ userId: s.userId, percentage: parseFloat(s.percentage) }));
      }
      await expenseAPI.create(payload);
      toast.success('Expense added! Settlements recalculated âœ“');
      setShowAddExp(false);
      loadAll();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to add expense');
    } finally { setSubmitting(false); }
  };

  const totalExpenses = expenses.reduce((s,e)=>s+Number(e.amount||0),0);
  const pendingSettlements = settlements.filter(s=>s.status!=='COMPLETED'&&s.status!=='PAID');

  if (loading) return (
    <div style={{padding:32}}>
      {[1,2,3].map(i=><div key={i} className="skeleton" style={{height:80,borderRadius:12,marginBottom:12}}/>)}
    </div>
  );
  if (!group) return <div style={{padding:32,color:'var(--text-muted)'}}>Group not found.</div>;

  return (
    <div style={S.page}>
      <button style={S.back} onClick={()=>navigate('/groups')}>â† Groups</button>

      <div style={S.header}>
        <div>
          <div style={S.groupType}>{group.type}</div>
          <h1 style={S.title}>{group.name}</h1>
          <p style={S.subtitle}>{group.description||''} Â· Invite: <span style={S.code}>#{group.inviteCode}</span></p>
        </div>
        <div style={S.headerStats}>
          <div style={S.statPill}>
            <span style={S.statPillLabel}>Total Spent</span>
            <span style={S.statPillVal}>â‚¹{totalExpenses.toLocaleString()}</span>
          </div>
          {pendingSettlements.length > 0 && (
            <div style={{...S.statPill, borderColor:'var(--danger)'}}>
              <span style={{...S.statPillLabel, color:'var(--danger)'}}>Pending Dues</span>
              <span style={{...S.statPillVal, color:'var(--danger)'}}>
                {pendingSettlements.length} settlement{pendingSettlements.length!==1?'s':''}
              </span>
            </div>
          )}
          <button className="btn btn-primary" onClick={()=>setShowAddExp(true)}>+ Add Expense</button>
        </div>
      </div>

      {/* Tabs */}
      <div style={S.tabs}>
        {[
          {key:'expenses',    label:`Expenses (${expenses.length})`},
          {key:'members',     label:`Members (${members.length})`},
          {key:'settlements', label:`Settlements (${pendingSettlements.length})`},
        ].map(t=>(
          <button key={t.key} style={{...S.tab,...(tab===t.key?S.tabActive:{})}} onClick={()=>setTab(t.key)}>
            {t.label}
          </button>
        ))}
      </div>

      {/* EXPENSES TAB */}
      {tab==='expenses' && (
        <div style={S.list}>
          {expenses.length===0 ? (
            <div style={S.empty}>
              <div style={{fontSize:40}}>â—Ž</div>
              <h3 style={S.emptyTitle}>No expenses yet</h3>
              <button className="btn btn-primary" onClick={()=>setShowAddExp(true)}>+ Add First Expense</button>
            </div>
          ) : expenses.map((exp,i)=>(
            <div key={exp.id||i} className="card" style={S.expRow}>
              <div style={S.expIcon}>{CAT_EMOJI[exp.category]||'â—Ž'}</div>
              <div style={{flex:1}}>
                <div style={S.expName}>{exp.description}</div>
                <div style={S.expMeta}>
                  Paid by <span style={{color:'var(--accent-bright)'}}>{exp.paidBy?.name||'?'}</span>
                  {' Â· '}{exp.splitType}
                  {' Â· '}{new Date(exp.expenseDate||exp.createdAt).toLocaleDateString('en-IN')}
                </div>
                {/* Show splits */}
                {exp.splits && exp.splits.length > 0 && (
                  <div style={S.splitsRow}>
                    {exp.splits.map((sp,j)=>(
                      <div key={j} style={S.splitChip}>
                        <span>{sp.user?.name?.split(' ')[0]}</span>
                        <span style={{color:'var(--accent-bright)',fontWeight:700}}>â‚¹{Number(sp.amount||0).toLocaleString()}</span>
                        {sp.isPaid && <span style={{color:'var(--success)',fontSize:10}}>âœ“</span>}
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div style={{textAlign:'right', flexShrink:0}}>
                <div style={S.expAmount}>â‚¹{Number(exp.amount||0).toLocaleString()}</div>
                {exp.yourShare!=null && <div style={S.yourShare}>Your share: â‚¹{Number(exp.yourShare).toLocaleString()}</div>}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* MEMBERS TAB */}
      {tab==='members' && (
        <div>
          <button className="btn btn-ghost" style={{marginBottom:16}} onClick={()=>setShowAddMember(true)}>
            + Add Member
          </button>
          <div style={S.list}>
            {members.map((m,i)=>(
              <div key={m.id} className="card" style={{...S.expRow, padding:'14px 20px'}}>
                <div className="avatar">{m.name?.[0]?.toUpperCase()||'?'}</div>
                <div style={{flex:1}}>
                  <div style={{fontWeight:600,color:'var(--text-primary)'}}>{m.name}</div>
                  <div style={{fontSize:12,color:'var(--text-muted)'}}>{m.email}</div>
                  {m.upiId && <div style={{fontSize:11,color:'var(--accent-bright)'}}>UPI: {m.upiId}</div>}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* SETTLEMENTS TAB */}
      {tab==='settlements' && (
        <div style={S.list}>
          {settlements.length===0 ? (
            <div style={S.empty}>
              <span style={{fontSize:32}}>ðŸŽ‰</span>
              <p style={{color:'var(--text-muted)',fontSize:14}}>All settled up! Add expenses to generate settlements.</p>
            </div>
          ) : settlements.map((s,i)=>(
            <div key={s.id||i} className="card" style={{...S.expRow, opacity: s.status==='COMPLETED'?0.6:1}}>
              <div className="avatar" style={{background:'rgba(255,107,107,0.15)',color:'var(--danger)'}}>{s.fromUser?.name?.[0]}</div>
              <span style={{color:'var(--text-muted)'}}>â†’</span>
              <div className="avatar" style={{background:'rgba(0,212,170,0.15)',color:'var(--success)'}}>{s.toUser?.name?.[0]}</div>
              <div style={{flex:1}}>
                <div style={S.expName}>
                  <b style={{color:'var(--text-primary)'}}>{s.fromUser?.name}</b>
                  <span style={{color:'var(--text-muted)'}}> owes </span>
                  <b style={{color:'var(--text-primary)'}}>{s.toUser?.name}</b>
                </div>
              </div>
              <div style={{textAlign:'right'}}>
                <div style={{fontFamily:'var(--font-display)',fontWeight:700,fontSize:18,color:s.status==='COMPLETED'?'var(--success)':'var(--danger)'}}>
                  â‚¹{Number(s.amount||0).toLocaleString()}
                </div>
                {s.status==='COMPLETED'
                  ? <span className="badge badge-success" style={{fontSize:11}}>Settled âœ“</span>
                  : <div style={S.settlePayBtns}>
                      {s.googlePayLink && <a href={s.googlePayLink} style={{...S.miniPayBtn,background:'#1a73e8'}}>GPay</a>}
                      {s.phonePeLink   && <a href={s.phonePeLink}   style={{...S.miniPayBtn,background:'#5f259f'}}>PhonePe</a>}
                    </div>
                }
              </div>
            </div>
          ))}
        </div>
      )}

      {/* â”€â”€â”€ ADD EXPENSE MODAL â”€â”€â”€ */}
      {showAddExp && (
        <div style={S.overlay} onClick={()=>setShowAddExp(false)}>
          <div style={S.modal} onClick={e=>e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Add Expense</h2>
            <form onSubmit={handleAddExpense} style={S.form}>
              <div className="input-group">
                <label>Description *</label>
                <input className="input" placeholder="e.g. Hotel Booking"
                  value={form.description} onChange={e=>setForm({...form,description:e.target.value})} required/>
              </div>
              <div style={{display:'grid',gridTemplateColumns:'1fr 1fr',gap:14}}>
                <div className="input-group">
                  <label>Amount (â‚¹) *</label>
                  <input className="input" type="number" min="0.01" step="0.01" placeholder="0.00"
                    value={form.amount} onChange={e=>setForm({...form,amount:e.target.value})} required/>
                </div>
                <div className="input-group">
                  <label>Category</label>
                  <select className="input" value={form.category} onChange={e=>setForm({...form,category:e.target.value})}>
                    {CATEGORIES.map(c=><option key={c} value={c}>{CAT_EMOJI[c]} {c}</option>)}
                  </select>
                </div>
              </div>

              {/* Split Type */}
              <div className="input-group">
                <label>How to Split?</label>
                <div style={S.splitTypeGrid}>
                  {Object.entries(SPLIT_INFO).map(([type,info])=>(
                    <button key={type} type="button"
                      style={{...S.splitTypeBtn,...(form.splitType===type?S.splitTypeBtnActive:{})}}
                      onClick={()=>changeSplitType(type)}>
                      <span style={{fontSize:18}}>{info.icon}</span>
                      <span style={{fontWeight:600,fontSize:12}}>{info.label}</span>
                      <span style={{fontSize:10,color:'var(--text-muted)',lineHeight:1.3}}>{info.desc}</span>
                    </button>
                  ))}
                </div>
              </div>

              {/* EQUAL / PARTIAL â€” choose participants */}
              {(form.splitType==='EQUAL'||form.splitType==='PARTIAL') && (
                <div className="input-group">
                  <label>
                    {form.splitType==='PARTIAL' ? 'Who pays? (select some)' : 'Split between:'}
                    {form.participantIds.length>0 && form.amount &&
                      <span style={{color:'var(--accent-bright)',fontWeight:600,marginLeft:8}}>
                        (â‚¹{getEqualShare()} each)
                      </span>
                    }
                  </label>
                  <div style={S.participantGrid}>
                    {members.map(m=>(
                      <button key={m.id} type="button"
                        style={{...S.partBtn,...(form.participantIds.includes(m.id)?S.partBtnActive:{})}}
                        onClick={()=>toggleParticipant(m.id)}>
                        <div style={S.partAvatar}>{m.name?.[0]?.toUpperCase()}</div>
                        {m.name?.split(' ')[0]}
                        {form.participantIds.includes(m.id) && <span style={{color:'var(--success)',fontSize:10}}>âœ“</span>}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* CUSTOM â€” enter amount per person */}
              {form.splitType==='CUSTOM' && (
                <div className="input-group">
                  <label>Enter amount per person
                    <span style={{color:'var(--text-muted)',fontWeight:400,marginLeft:6,fontSize:12}}>
                      (must total â‚¹{form.amount||'...'})
                    </span>
                  </label>
                  {members.map(m=>(
                    <div key={m.id} style={{display:'flex',alignItems:'center',gap:10,marginBottom:8}}>
                      <div className="avatar" style={{flexShrink:0,width:30,height:30,fontSize:12}}>{m.name?.[0]}</div>
                      <span style={{flex:1,fontSize:13,color:'var(--text-secondary)'}}>{m.name}</span>
                      <input className="input" type="number" min="0" step="0.01" placeholder="0.00"
                        style={{width:100,padding:'6px 10px'}}
                        value={form.customSplits.find(s=>s.userId===m.id)?.amount||''}
                        onChange={e=>setForm(f=>({...f,customSplits:f.customSplits.map(s=>s.userId===m.id?{...s,amount:e.target.value}:s)}))}/>
                    </div>
                  ))}
                  <div style={{fontSize:12,color:
                    Math.abs(form.customSplits.reduce((s,x)=>s+parseFloat(x.amount||0),0)-parseFloat(form.amount||0))<0.5
                    ?'var(--success)':'var(--warning)'}}>
                    Sum: â‚¹{form.customSplits.reduce((s,x)=>s+parseFloat(x.amount||0),0).toFixed(2)} / â‚¹{form.amount||0}
                  </div>
                </div>
              )}

              {/* PERCENTAGE â€” enter % per person */}
              {form.splitType==='PERCENTAGE' && (
                <div className="input-group">
                  <label>Enter % per person <span style={{color:'var(--text-muted)',fontWeight:400,fontSize:12}}>(must total 100%)</span></label>
                  {members.map(m=>(
                    <div key={m.id} style={{display:'flex',alignItems:'center',gap:10,marginBottom:8}}>
                      <div className="avatar" style={{flexShrink:0,width:30,height:30,fontSize:12}}>{m.name?.[0]}</div>
                      <span style={{flex:1,fontSize:13,color:'var(--text-secondary)'}}>{m.name}</span>
                      <div style={{display:'flex',alignItems:'center',gap:4}}>
                        <input className="input" type="number" min="0" max="100" step="1" placeholder="0"
                          style={{width:80,padding:'6px 10px'}}
                          value={form.percentageSplits.find(s=>s.userId===m.id)?.percentage||''}
                          onChange={e=>setForm(f=>({...f,percentageSplits:f.percentageSplits.map(s=>s.userId===m.id?{...s,percentage:e.target.value}:s)}))}/>
                        <span style={{fontSize:13,color:'var(--text-muted)'}}>%</span>
                        {form.percentageSplits.find(s=>s.userId===m.id)?.percentage && form.amount && (
                          <span style={{fontSize:11,color:'var(--accent-bright)',minWidth:50}}>
                            â‚¹{(parseFloat(form.amount||0)*parseFloat(form.percentageSplits.find(s=>s.userId===m.id).percentage||0)/100).toFixed(2)}
                          </span>
                        )}
                      </div>
                    </div>
                  ))}
                  <div style={{fontSize:12,color:
                    Math.abs(form.percentageSplits.reduce((s,x)=>s+parseFloat(x.percentage||0),0)-100)<0.5
                    ?'var(--success)':'var(--warning)'}}>
                    Total: {form.percentageSplits.reduce((s,x)=>s+parseFloat(x.percentage||0),0).toFixed(1)}%
                  </div>
                </div>
              )}

              <div style={S.formActions}>
                <button type="button" className="btn btn-ghost" onClick={()=>setShowAddExp(false)}>Cancel</button>
                <button type="submit" className="btn btn-primary" disabled={submitting}>
                  {submitting?'Adding...':'Add Expense â†’'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* â”€â”€â”€ ADD MEMBER MODAL â”€â”€â”€ */}
      {showAddMember && (
        <div style={S.overlay} onClick={()=>setShowAddMember(false)}>
          <div style={{...S.modal,maxWidth:400}} onClick={e=>e.stopPropagation()} className="animate-fadeUp">
            <h2 style={S.modalTitle}>Add Member</h2>
            <div style={{background:'var(--bg-secondary)',borderRadius:10,padding:'12px 14px',marginBottom:16,fontSize:13}}>
              Share invite code:{' '}
              <span style={{color:'var(--accent-bright)',fontWeight:700,fontFamily:'monospace'}}>#{group.inviteCode}</span>
              <br/><span style={{color:'var(--text-muted)',fontSize:12}}>Members can join at /join/code</span>
            </div>
            <input className="input" placeholder="Search by name or email..."
              value={searchQ} onChange={e=>handleSearch(e.target.value)} style={{marginBottom:10}}/>
            <div style={{display:'flex',flexDirection:'column',gap:8,maxHeight:250,overflowY:'auto'}}>
              {searchRes.map(u=>(
                <div key={u.id} style={{display:'flex',alignItems:'center',gap:10,padding:'10px 12px',background:'var(--bg-secondary)',borderRadius:10}}>
                  <div className="avatar">{u.name?.[0]?.toUpperCase()}</div>
                  <div style={{flex:1}}>
                    <div style={{fontWeight:600,fontSize:13,color:'var(--text-primary)'}}>{u.name}</div>
                    <div style={{fontSize:11,color:'var(--text-muted)'}}>{u.email}</div>
                  </div>
                  <button className="btn btn-primary" style={{padding:'6px 12px',fontSize:12}} onClick={()=>addMember(u.id)}>Add</button>
                </div>
              ))}
              {searchQ.length>=2 && searchRes.length===0 && (
                <p style={{color:'var(--text-muted)',fontSize:13,textAlign:'center',padding:'16px 0'}}>No users found</p>
              )}
            </div>
            <button className="btn btn-ghost" style={{marginTop:14,width:'100%'}} onClick={()=>setShowAddMember(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

const S = {
  page:         { padding:32, maxWidth:950, animation:'fadeUp 0.4s ease' },
  back:         { background:'none', border:'none', color:'var(--text-muted)', cursor:'pointer', fontSize:13, padding:'0 0 16px 0', display:'block' },
  header:       { display:'flex', justifyContent:'space-between', alignItems:'flex-start', marginBottom:24, flexWrap:'wrap', gap:12 },
  groupType:    { fontSize:11, color:'var(--accent-bright)', fontWeight:700, letterSpacing:'1px', marginBottom:4 },
  title:        { fontFamily:'var(--font-display)', fontWeight:800, fontSize:26, color:'var(--text-primary)', marginBottom:4 },
  subtitle:     { color:'var(--text-muted)', fontSize:13 },
  code:         { color:'var(--accent-bright)', fontFamily:'monospace' },
  headerStats:  { display:'flex', gap:10, alignItems:'center', flexWrap:'wrap' },
  statPill:     { background:'var(--bg-card)', border:'1px solid var(--border)', borderRadius:12, padding:'8px 14px', display:'flex', flexDirection:'column', alignItems:'flex-end' },
  statPillLabel:{ fontSize:11, color:'var(--text-muted)' },
  statPillVal:  { fontFamily:'var(--font-display)', fontWeight:700, fontSize:16, color:'var(--accent-bright)' },
  tabs:         { display:'flex', gap:8, marginBottom:20 },
  tab:          { padding:'8px 18px', borderRadius:20, border:'1px solid var(--border)', background:'transparent', color:'var(--text-secondary)', cursor:'pointer', fontSize:13, fontFamily:'var(--font-body)', transition:'all 0.15s' },
  tabActive:    { background:'var(--accent-dim)', borderColor:'var(--accent)', color:'var(--accent-bright)' },
  list:         { display:'flex', flexDirection:'column', gap:10 },
  expRow:       { display:'flex', alignItems:'center', gap:14, padding:'16px 20px' },
  expIcon:      { fontSize:22, width:46, height:46, borderRadius:12, background:'var(--bg-secondary)', display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0 },
  expName:      { fontWeight:600, fontSize:14, color:'var(--text-primary)', marginBottom:3 },
  expMeta:      { fontSize:12, color:'var(--text-muted)' },
  expAmount:    { fontFamily:'var(--font-display)', fontWeight:700, fontSize:18, color:'var(--text-primary)' },
  yourShare:    { fontSize:11, color:'var(--text-muted)', marginTop:2 },
  splitsRow:    { display:'flex', gap:6, flexWrap:'wrap', marginTop:6 },
  splitChip:    { display:'flex', gap:4, alignItems:'center', fontSize:11, padding:'3px 8px', background:'var(--bg-secondary)', borderRadius:20, color:'var(--text-muted)' },
  settlePayBtns:{ display:'flex', gap:4, marginTop:4 },
  miniPayBtn:   { fontSize:10, padding:'3px 8px', borderRadius:6, color:'white', textDecoration:'none', fontWeight:600 },
  empty:        { display:'flex', flexDirection:'column', alignItems:'center', gap:12, padding:'60px 0', textAlign:'center' },
  emptyTitle:   { fontFamily:'var(--font-display)', fontWeight:700, fontSize:18, color:'var(--text-primary)' },
  overlay:      { position:'fixed', inset:0, background:'rgba(0,0,0,0.75)', backdropFilter:'blur(4px)', display:'flex', alignItems:'center', justifyContent:'center', zIndex:1000, padding:24 },
  modal:        { background:'var(--bg-card)', border:'1px solid var(--border)', borderRadius:'var(--radius-lg)', padding:28, width:'100%', maxWidth:540, maxHeight:'92vh', overflowY:'auto' },
  modalTitle:   { fontFamily:'var(--font-display)', fontWeight:700, fontSize:20, color:'var(--text-primary)', marginBottom:20 },
  form:         { display:'flex', flexDirection:'column', gap:18 },
  splitTypeGrid:{ display:'grid', gridTemplateColumns:'repeat(2,1fr)', gap:8, marginTop:8 },
  splitTypeBtn: { display:'flex', flexDirection:'column', alignItems:'center', gap:4, padding:'12px 10px', borderRadius:12, border:'1px solid var(--border)', background:'var(--bg-secondary)', cursor:'pointer', textAlign:'center', transition:'all 0.15s', color:'var(--text-secondary)' },
  splitTypeBtnActive: { background:'var(--accent-dim)', borderColor:'var(--accent)', color:'var(--accent-bright)' },
  participantGrid:{ display:'flex', flexWrap:'wrap', gap:8, marginTop:8 },
  partBtn:      { display:'flex', alignItems:'center', gap:8, padding:'7px 12px', borderRadius:20, border:'1px solid var(--border)', background:'var(--bg-secondary)', cursor:'pointer', fontSize:13, fontFamily:'var(--font-body)', transition:'all 0.15s', color:'var(--text-secondary)' },
  partBtnActive:{ background:'var(--accent-dim)', borderColor:'var(--accent)', color:'var(--accent-bright)' },
  partAvatar:   { width:22, height:22, borderRadius:'50%', background:'var(--accent-dim)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:11, fontWeight:700 },
  formActions:  { display:'flex', gap:10, justifyContent:'flex-end', marginTop:4 },
};
