import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { groupAPI } from '../services/api';
import toast from 'react-hot-toast';

const categories = ['TRIP', 'HOME', 'FOOD', 'ENTERTAINMENT', 'OTHER'];
const categoryEmoji = { TRIP: 'âœˆï¸', HOME: 'ðŸ ', FOOD: 'ðŸ½ï¸', ENTERTAINMENT: 'ðŸŽ®', OTHER: 'â—Ž' };

export default function Groups() {
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreate, setShowCreate] = useState(false);
  const [form, setForm] = useState({ name: '', description: '', category: 'TRIP' });
  const [creating, setCreating] = useState(false);
  const navigate = useNavigate();

  useEffect(() => { loadGroups(); }, []);

  const loadGroups = async () => {
    setLoading(true);
    try {
      const data = await groupAPI.getAll();
      setGroups(Array.isArray(data) ? data : (data?.content || mockGroups));
    } catch {
      setGroups(mockGroups);
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setCreating(true);
    try {
      await groupAPI.create(form);
      toast.success('Group created! ðŸŽ‰');
      setShowCreate(false);
      setForm({ name: '', description: '', category: 'TRIP' });
      loadGroups();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to create group');
    } finally {
      setCreating(false);
    }
  };

  return (
    <div style={styles.page}>
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>Groups</h1>
          <p style={styles.subtitle}>{groups.length} active group{groups.length !== 1 ? 's' : ''}</p>
        </div>
        <button className="btn btn-primary" onClick={() => setShowCreate(true)}>+ New Group</button>
      </div>

      {showCreate && (
        <div style={styles.modalOverlay} onClick={() => setShowCreate(false)}>
          <div style={styles.modal} onClick={e => e.stopPropagation()} className="animate-fadeUp">
            <h2 style={styles.modalTitle}>Create Group</h2>
            <form onSubmit={handleCreate} style={styles.form}>
              <div className="input-group">
                <label>Group Name</label>
                <input className="input" placeholder="Goa Trip 2025" value={form.name}
                  onChange={e => setForm({ ...form, name: e.target.value })} required />
              </div>
              <div className="input-group">
                <label>Description (optional)</label>
                <input className="input" placeholder="What's this group for?" value={form.description}
                  onChange={e => setForm({ ...form, description: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Category</label>
                <div style={styles.categoryGrid}>
                  {categories.map(cat => (
                    <button key={cat} type="button"
                      style={{ ...styles.catBtn, ...(form.category === cat ? styles.catBtnActive : {}) }}
                      onClick={() => setForm({ ...form, category: cat })}>
                      {categoryEmoji[cat]} {cat}
                    </button>
                  ))}
                </div>
              </div>
              <div style={styles.modalActions}>
                <button type="button" className="btn btn-ghost" onClick={() => setShowCreate(false)}>Cancel</button>
                <button type="submit" className="btn btn-primary" disabled={creating}>
                  {creating ? 'Creating...' : 'Create Group'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading ? (
        <div style={styles.grid}>
          {[1,2,3,4].map(i => <div key={i} className="skeleton" style={{ height: 180, borderRadius: 16 }} />)}
        </div>
      ) : groups.length === 0 ? (
        <div style={styles.empty}>
          <div style={styles.emptyIcon}>â—ˆ</div>
          <h3 style={styles.emptyTitle}>No groups yet</h3>
          <p style={styles.emptyText}>Create a group to start splitting expenses with friends</p>
          <button className="btn btn-primary" onClick={() => setShowCreate(true)}>Create your first group</button>
        </div>
      ) : (
        <div style={styles.grid}>
          {groups.map((group, i) => (
            <div key={group.id || i} className="card" style={styles.groupCard}
              onClick={() => navigate(`/groups/${group.id}`)}>
              <div style={styles.groupTop}>
                <div style={styles.groupIcon}>{categoryEmoji[group.category] || 'â—Ž'}</div>
                <span className="badge badge-accent">{group.category || 'GROUP'}</span>
              </div>
              <h3 style={styles.groupName}>{group.name}</h3>
              <p style={styles.groupDesc}>{group.description || 'No description'}</p>
              <div style={styles.groupFooter}>
                <div style={styles.memberCount}>
                  <span style={{ color: 'var(--accent)', fontSize: 10 }}>â—‰</span>
                  {group.memberCount || group.members?.length || 1} members
                </div>
                <span style={styles.groupAmount}>
                  â‚¹{(group.totalExpenses || 0).toLocaleString()}
                </span>
              </div>
              <div style={styles.groupGlow} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const mockGroups = [
  { id: 1, name: 'Goa Trip 2025', category: 'TRIP', description: 'Beach holiday with friends', memberCount: 6, totalExpenses: 45000 },
  { id: 2, name: 'Flat Expenses', category: 'HOME', description: 'Monthly household bills', memberCount: 3, totalExpenses: 12500 },
  { id: 3, name: 'Office Lunch', category: 'FOOD', description: 'Daily team lunches', memberCount: 8, totalExpenses: 8200 },
  { id: 4, name: 'Weekend Trek', category: 'TRIP', description: 'Coorg hiking trip', memberCount: 4, totalExpenses: 22000 },
];

const styles = {
  page: { padding: 32, maxWidth: 1200, animation: 'fadeUp 0.4s ease' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: 32 },
  title: { fontFamily: 'var(--font-display)', fontWeight: 800, fontSize: 30, color: 'var(--text-primary)', marginBottom: 4 },
  subtitle: { color: 'var(--text-muted)', fontSize: 14 },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))', gap: 20 },
  groupCard: { cursor: 'pointer', position: 'relative', overflow: 'hidden', transition: 'transform 0.2s', },
