import React, { useState, useEffect } from 'react';
import { aiAPI, groupAPI } from '../services/api';
import toast from 'react-hot-toast';

const INSIGHT_META = {
  HEAVY_SPENDING:   { icon: 'üìà', color: '#ff6b6b', label: 'High Spending'   },
  PAYMENT_DELAY:    { icon: '‚è∞', color: '#ffd93d', label: 'Payment Delay'   },
  MONTHLY_TREND:    { icon: 'üìä', color: '#6c63ff', label: 'Monthly Trend'   },
  COST_SAVING:      { icon: 'üí°', color: '#00d4aa', label: 'Saving Tip'      },
  GENERAL:          { icon: '‚ú¶',  color: '#9b93ff', label: 'Insight'         },
};

export default function AIAssistant() {
  const [tab, setTab]               = useState('insights'); // insights | tips
  const [insights, setInsights]     = useState([]);
  const [loading, setLoading]       = useState(false);
  const [analyzing, setAnalyzing]   = useState(false);
  const [groups, setGroups]         = useState([]);

  useEffect(() => {
    loadInsights();
    groupAPI.getAll().then(g => setGroups(Array.isArray(g) ? g : []));
  }, []);

  const loadInsights = async () => {
    setLoading(true);
    const data = await aiAPI.getInsights();
    setInsights(Array.isArray(data) ? data : []);
    setLoading(false);
  };

  const runAnalysis = async () => {
    setAnalyzing(true);
    toast.loading('Running AI analysis...', { id: 'ai' });
    try {
      await aiAPI.triggerAnalyze();
      toast.success('Analysis complete! Loading insights...', { id: 'ai' });
      await loadInsights();
    } catch {
      toast.error('Analysis failed', { id: 'ai' });
    } finally { setAnalyzing(false); }
  };

  const markRead = async (id) => {
    await aiAPI.markRead(id);
    setInsights(prev => prev.map(i => i.id === id ? { ...i, isRead: true } : i));
  };

  const unreadCount = insights.filter(i => !i.isRead).length;

  const TIPS = [
    { icon: 'üéØ', title: 'Split equally for simple bills', desc: 'Use Equal split for restaurant bills, movie tickets or shared rides where everyone benefits the same.' },
    { icon: 'üì∏', title: 'Add expenses immediately', desc: 'Add the expense right after paying so you don\'t forget. It takes 10 seconds!' },
    { icon: 'üí∞', title: 'Settle weekly', desc: 'Settle up every week instead of letting debts pile up. Smaller amounts are easier to manage.' },
    { icon: 'üîî', title: 'Set your UPI ID', desc: 'Set your UPI ID in Profile so others can pay you directly via GPay or PhonePe without asking.' },
    { icon: 'üìä', title: 'Use the right split type', desc: 'Custom split for unequal bills, Percentage for tip sharing, Individual items for grocery splits.' },
    { icon: '‚≠ê', title: 'Earn reward points', desc: 'Pay your settlements quickly to earn reward points and build your zero-debt streak!' },
  ];

  return (
    <div style={S.page}>
      <div style={S.header}>
        <div>
          <h1 style={S.title}>‚ú¶ AI Assistant</h1>
          <p style={S.sub}>Smart insights and tips for better expense management</p>
        </div>
        <button className="btn btn-primary" onClick={runAnalysis} disabled={analyzing}
          style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          {analyzing ? '‚è≥ Analyzing...' : '‚ö° Run AI Analysis'}
        </button>
      </div>

      {/* Tabs */}
      <div style={S.tabs}>
        <button style={{ ...S.tab, ...(tab === 'insights' ? S.tabOn : {}) }}
          onClick={() => setTab('insights')}>
          ‚ú¶ AI Insights
          {unreadCount > 0 && <span style={S.badge}>{unreadCount}</span>}
        </button>
        <button style={{ ...S.tab, ...(tab === 'tips' ? S.tabOn : {}) }}
          onClick={() => setTab('tips')}>
          üí° Smart Tips
        </button>
      </div>

      {/* ‚îÄ‚îÄ INSIGHTS TAB ‚îÄ‚îÄ */}
      {tab === 'insights' && (
        <div>
          {loading ? (
            <div style={S.grid}>
              {[1,2,3].map(i => <div key={i} className="skeleton" style={{ height: 140, borderRadius: 16 }} />)}
            </div>
          ) : insights.length === 0 ? (
            <div style={S.empty}>
              <div style={{ fontSize: 52, marginBottom: 16 }}>ü§ñ</div>
              <h3 style={S.emptyTitle}>No insights yet</h3>
              <p style={{ color: 'var(--text-muted)', fontSize: 14, marginBottom: 24, maxWidth: 360, textAlign: 'center' }}>
                Click <b>"Run AI Analysis"</b> to generate personalised spending insights based on your expense history.
              </p>
              <button className="btn btn-primary" onClick={runAnalysis} disabled={analyzing}>
                {analyzing ? '‚è≥ Analyzing...' : '‚ö° Generate My Insights'}
              </button>
            </div>
          ) : (
            <>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 }}>
                <span style={{ fontSize: 13, color: 'var(--text-muted)' }}>{insights.length} insights ¬∑ {unreadCount} unread</span>
                <button className="btn btn-ghost" style={{ fontSize: 12, padding: '6px 14px' }} onClick={runAnalysis} disabled={analyzing}>
                  {analyzing ? 'Analyzing...' : '‚Üª Refresh'}
                </button>
              </div>
              <div style={S.grid}>
                {insights.map((ins, i) => {
                  const meta = INSIGHT_META[ins.insightType] || INSIGHT_META.GENERAL;
                  return (
                    <div key={ins.id || i} className="card"
                      style={{ ...S.card, ...(!ins.isRead ? S.cardUnread : {}), borderLeft: `3px solid ${meta.color}` }}
                      onClick={() => !ins.isRead && markRead(ins.id)}>
                      <div style={S.cardTop}>
                        <div style={{ ...S.insightIcon, background: meta.color + '22', color: meta.color }}>
                          {meta.icon}
                        </div>
                        <div style={{ flex: 1 }}>
                          <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                            <span style={{ ...S.typeLabel, color: meta.color }}>{meta.label}</span>
                            {!ins.isRead && <span style={S.newBadge}>NEW</span>}
                          </div>
                          <div style={S.insightMsg}>{ins.message}</div>
                        </div>
                      </div>
                      {ins.createdAt && (
                        <div style={S.insightTime}>
                          {new Date(ins.createdAt).toLocaleDateString('en-IN', { day: 'numeric', month: 'short', hour: '2-digit', minute: '2-digit' })}
                          {!ins.isRead && <span style={{ color: 'var(--text-muted)', marginLeft: 8 }}>¬∑ click to mark read</span>}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </>
          )}
        </div>
      )}

      {/* ‚îÄ‚îÄ TIPS TAB ‚îÄ‚îÄ */}
      {tab === 'tips' && (
        <div style={S.grid}>
          {TIPS.map((tip, i) => (
            <div key={i} className="card" style={S.tipCard}>
              <div style={S.tipIcon}>{tip.icon}</div>
              <h3 style={S.tipTitle}>{tip.title}</h3>
              <p style={S.tipDesc}>{tip.desc}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

