import React, { useState, useEffect } from 'react';
import { settlementAPI, groupAPI } from '../services/api';
import toast from 'react-hot-toast';

export default function Settlements() {
  const [groups, setGroups]           = useState([]);
  const [settlements, setSettlements] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [loading, setLoading]         = useState(false);
  const [paying, setPaying]           = useState(null);

  useEffect(() => { loadGroups(); }, []);
  useEffect(() => { if (selectedGroup) loadSettlements(selectedGroup); }, [selectedGroup]);

  const loadGroups = async () => {
    try {
      const data = await groupAPI.getAll();
      const list = Array.isArray(data) ? data : [];
      setGroups(list);
      if (list.length > 0) setSelectedGroup(list[0].id);
    } catch { setGroups([]); }
  };

  const loadSettlements = async (groupId) => {
    setLoading(true);
    try {
      const data = await settlementAPI.getByGroup(groupId);
      setSettlements(Array.isArray(data) ? data : []);
    } catch { setSettlements([]); }
    finally { setLoading(false); }
  };

  const handleSettle = async (id) => {
    setPaying(id);
    try {
      // Backend uses /settle not /pay
      await settlementAPI.markSettled(id);
      toast.success('Settlement marked as paid! ðŸŽ‰');
      setSettlements(prev => prev.map(s => s.id === id ? {...s, status:'COMPLETED'} : s));
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to settle');
    } finally { setPaying(null); }
  };

  const pending = settlements.filter(s => s.status !== 'COMPLETED' && s.status !== 'PAID');
  const done    = settlements.filter(s => s.status === 'COMPLETED' || s.status === 'PAID');
  const total   = pending.reduce((s, x) => s + (x.amount || 0), 0);

  return (
    <div style={S.page}>
      <div style={S.header}>
        <div>
          <h1 style={S.title}>Settlements</h1>
          <p style={S.subtitle}>Simplified debt repayment</p>
        </div>
        {total > 0 && (
          <div style={S.totalBadge}>
            <span style={S.totalLabel}>PENDING</span>
            <span style={S.totalAmt}>â‚¹{total.toLocaleString()}</span>
          </div>
        )}
      </div>

      {/* Group tabs */}
      {groups.length > 0 && (
        <div style={S.tabs}>
          {groups.map(g => (
            <button key={g.id}
              style={{...S.tab, ...(selectedGroup===g.id ? S.tabActive:{})}}
              onClick={() => setSelectedGroup(g.id)}>
              {g.name}
            </button>
          ))}
        </div>
      )}

      {loading ? (
        [1,2,3].map(i => <div key={i} className="skeleton" style={{height:90, borderRadius:16, marginBottom:12}} />)
      ) : settlements.length === 0 ? (
        <div style={S.empty}>
          <div style={{fontSize:48}}>ðŸŽ‰</div>
          <h3 style={S.emptyTitle}>All settled up!</h3>
          <p style={S.emptyText}>No pending payments in this group. Add expenses to generate settlements.</p>
        </div>
      ) : (
        <>
          {pending.length > 0 && (
            <div style={S.section}>
              <div style={S.sectionHead}>
                <span style={S.dotRed} /> Pending Â· {pending.length}
              </div>
              {pending.map((s, i) => (
                <div key={s.id || i} className="card" style={S.card}>
                  <div style={S.left}>
                    <div className="avatar" style={S.avatarFrom}>{s.fromUser?.name?.[0] || 'A'}</div>
                    <span style={S.arrow}>â†’</span>
                    <div className="avatar" style={S.avatarTo}>{s.toUser?.name?.[0] || 'B'}</div>
                    <div>
                      <div style={S.names}>
                        <b style={{color:'var(--text-primary)'}}>{s.fromUser?.name}</b>
                        <span style={{color:'var(--text-muted)'}}> owes </span>
                        <b style={{color:'var(--text-primary)'}}>{s.toUser?.name}</b>
                      </div>
                      {s.groupName && <div style={S.groupTag}>{s.groupName}</div>}
                    </div>
                  </div>
                  <div style={S.right}>
                    <div style={S.amtRed}>â‚¹{Number(s.amount||0).toLocaleString()}</div>
                    <button className="btn btn-primary" style={S.payBtn}
                      disabled={paying === s.id} onClick={() => handleSettle(s.id)}>
                      {paying === s.id ? '...' : 'Mark Paid âœ“'}
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}

          {done.length > 0 && (
            <div style={S.section}>
              <div style={S.sectionHead}>
                <span style={S.dotGreen} /> Settled Â· {done.length}
              </div>
              {done.map((s, i) => (
                <div key={s.id || i} className="card" style={{...S.card, opacity:0.55}}>
                  <div style={S.left}>
                    <div className="avatar">{s.fromUser?.name?.[0] || 'A'}</div>
                    <span style={S.arrow}>â†’</span>
                    <div className="avatar">{s.toUser?.name?.[0] || 'B'}</div>
                    <div style={S.names}>
                      <b style={{color:'var(--text-primary)'}}>{s.fromUser?.name}</b>
                      <span style={{color:'var(--text-muted)'}}> paid </span>
                      <b style={{color:'var(--text-primary)'}}>{s.toUser?.name}</b>
                    </div>
                  </div>
                  <div style={S.right}>
                    <div style={{...S.amtRed, color:'var(--success)'}}>â‚¹{Number(s.amount||0).toLocaleString()}</div>
                    <span className="badge badge-success">Settled âœ“</span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

